@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");
  a{
    cursor: inherit;
    transition:all 0.4s linear;
  }
  a:hover {
     transform: scale(1.1)
  }

#page {
  background-color: #CBE3EC;
  background-image: url("https://edqjpryjjnqukkwhzcjp.supabase.co/storage/v1/object/public/images/water_bg.png?t=2023-10-15T23%3A27%3A41.477Z");
  background-position: top left;
  background-size: cover;
  background-attachment: scroll;
  background-repeat: repeat; 
}

@import url("https://use.typekit.net/bpd6gjo.css")

#page {
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

[data-block]{
  position: unset !important;
  min-height: unset !important;
}

:root {
  --white: #f4f4f4;
  --red: #c42727;
  --taupe: #e1dac1;
  --brown: #3e281a;
  --green: #304d2d;
  --blue: #112e45;
}

button {
  font-family: "canto-brush", serif;
  font-size: 36px;
  text-transform: lowercase;
}

img {
  display: block;
  width: 800px;
  height: auto;
  max-height: 100%;
  object-fit: contain;
  margin: 0 auto;
}

div {
  max-height: 600px;
  margin: 32px 0;
  padding: 0 12px;
}

.button-container {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 16px;

/*   desktop styles */
  @media only screen and (min-width: 768px){
    flex-direction: row;
    margin-top: 24px;
  }
}

button {
  color: var(--green);
  margin: 0 12px;
/*   desktop styles */
  @media only screen and (min-width: 768px){
    margin: 0 24px;
    cursor: pointer;
  }
}

img {
  width: 100%;
  height: 250px;
  object-fit: contain;
  max-width: 800px;
  margin: 0 auto;
  padding: 24px 24px 0;
}

header {
  padding-top: 32px;
  /*   desktop styles */
  @media only screen and (min-width: 768px){
/*     padding-top: 100px; */
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
  justify-content: center;
  box-sizing: border-box;
  margin-bottom: 32px;
}

.link { /* Defined in Site CSS */
  font-weight: 500;
  border-color: transparent;
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: initial;
  }
}

ul {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1.5rem;

  li {
    color: var(--blue);  
    background: transparent; 
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
/*     border-radius: 50%; */
    transition: 0.1s transform;
    cursor: pointer;

    &:hover {
      transform: scale(1.1);
    }

    a {
      /*   desktop styles */
      @media only screen and (min-width: 768px){
          font-size: 2rem;
          padding: 12px;

      }
      font-size: 1.5rem;
    }
  }
}
