@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");
  a{
    cursor: inherit;
    transition:all 0.4s linear;
  }
  a:hover {
     transform: scale(1.1)
  }

#page {
  background-color: var(--taupe);
  background-image: url("https://edqjpryjjnqukkwhzcjp.supabase.co/storage/v1/object/public/images/textured_beige.png?t=2023-10-16T18%3A14%3A36.512Z");
  background-position: top left;
  background-size: cover;
  background-attachment: scroll;
  background-repeat: repeat;

  height: 100vh;
  max-height: 100%;
  padding: 16px 0;
}

@import url("https://use.typekit.net/bpd6gjo.css");
#page {
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

[data-block]{
  position: unset !important;
  min-height: unset !important;
}

:root {
  --white: #f4f4f4;
  --red: #c42727;
  --taupe: #e1dac1;
  --brown: #3e281a;
  --green: #304d2d;
  --blue: #112e45;
}

button {
  font-family: "canto-brush", serif;
  font-size: 36px;
  font-style:italic;
  text-transform: lowercase;
}

#mc_embed_signup {
  width: 100%;
  background: transparent;
  color: var(--blue);
  padding: 0 32px;
  #mc_embed_signup_scroll {
    max-width: 400px;
    margin: 0 auto;
  }
  form {
    max-width: 600px;
    margin: 0 auto;
    label {
      font-size: 14px;
    }
    input {
      background: transparent;
      border: 1px solid var(--green);
    }
  }
  .indicates-required {
    margin: 0;
  }

  h2 {
    /*   desktop styles */
    @media only screen and (min-width: 768px){
      font-size: 30px;

    }
    font-size: 18px;
    margin: 0;
    font-family: "canto-brush", serif;
    font-style:italic;
    text-align: center;
  }
  #mc-embedded-subscribe {
    width: fit-content;
    background: var(--green);
    height: 40px;
  }
  #mce-EMAIL-HELPERTEXT {
    background: transparent;
  }
  .foot {
    width: 100%;
    display: flex;
    justify-content: space-between;
  }
}

#mc_embed_shell {
  margin: 52px 0;
  /*   desktop styles */
  @media only screen and (min-width: 768px){
    margin: 32px 0;
  }

}

img {
  display: block;
  width: 800px;
  height: auto;
  max-height: 100%;
  object-fit: contain;
  margin: 0 auto;
}

div {
  max-height: 600px;
  margin: 32px 0;
  padding: 0 12px;
}

.button-container {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 16px;

/*   desktop styles */
  @media only screen and (min-width: 768px){
    flex-direction: row;
    margin-top: 24px;
  }
}

button {
  color: var(--green);
  margin: 0 12px;
/*   desktop styles */
  @media only screen and (min-width: 768px){
    margin: 0 24px;
    cursor: pointer;
  }
}

img {
  width: 100%;
  max-height: 250px;
  object-fit: contain;
  max-width: 800px;
  margin: 0 auto;
}
h1 {
  position: absolute;
  top: 50%;
  right: 50%;
  display: none;
}

header {
  position: relative;
  margin: 24px;
}

.section-container { /* Defined in Site CSS */
  display: grid;
  justify-content: center;
  box-sizing: border-box;
  margin-bottom: 32px;
}

.link { /* Defined in Site CSS */
  font-weight: 500;
  border-color: transparent;
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: initial;
  }
}

ul {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1.5rem;

  li {
    color: var(--blue);  
    background: transparent; 
    font-size: 1.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
/*     border-radius: 50%; */
    transition: 0.1s transform;
    cursor: pointer;

    &:hover {
      transform: scale(1.1);
    }

    a {
      /*   desktop styles */
      @media only screen and (min-width: 768px){
          font-size: 2rem;
          padding: 12px;

      }
      font-size: 1.5rem;
    }
  }
}
